if(window.history.replaceState)
{
    window.history.replaceState(null, null, window.location.href);
}

/**************************************************
***** @filename main.core.min.js = > tolocale.js **
**************************************************/

$('.uppercase').keyup(function()
{
    this.value=this.value.toLocaleUpperCase("tr-TR");
});

$('.lowercase').on('input', function() 
{
    this.value = this.value.normalize('NFKC').toLowerCase();
});

/**************************************************
*** @filename main.core.min.js = > upload-img.js **
**************************************************/

$(document).ready(function(){
    $('#image').change(function(e){
        var reader = new FileReader();
        reader.onload = function(e){
            $('#ProfileImage').attr('src',e.target.result);
        }
        reader.readAsDataURL(e.target.files['0']);
    });
});

/**************************************************
*** @filename main.core.min.js = > showImage.js **
**************************************************/

function showImage() {
    var input = document.getElementById('image');
    var preview = document.getElementById('previewImage');
    
    var file = input.files[0];
    var reader = new FileReader();

    reader.onload = function() {
        preview.src = reader.result;
    }

    if (file) {
        reader.readAsDataURL(file);
    } else {
        preview.src = "";
    }
}