/**************************************************
***** @filename main.core.min.js => toggle.js *****
**************************************************/

$(document).ready(function() 
{
    document.getElementById("menu").style.display = "flex";

    $("#close-menu").click(function() 
    { 

        if(document.getElementById("menu").style.display == "flex")
        {
            document.getElementById("menu").style.display = "none";
            document.getElementById("layout-wrapper").style.marginLeft = "0px";

            document.getElementById("arrow-button").classList.remove("bi-arrow-left");
            document.getElementById("arrow-button").classList.toggle("bi-arrow-right");
        }
        else
        {
            document.getElementById("menu").style.display = "flex";
            document.getElementById("layout-wrapper").style.marginLeft = "350px";

            document.getElementById("arrow-button").classList.remove("bi-arrow-right");
            document.getElementById("arrow-button").classList.toggle("bi-arrow-left");
        }
    });

    $("#close-menu").hover(function() 
    {
        if (document.getElementById("menu").style.display == "flex") 
        {
            $(this).attr("title", "Menüyü Daralt");
        }
        else 
        {
            $(this).attr("title", "Menüyü Genişlet");
        }
    });
});

